$.exports.lazy = $('').lazyRule(() => {
    try {
        input = input.split("##")[1];
        //log(input)

        function getvideo(url) {
            var t = new Date().getTime().toString();
            let jData = request(url, {
                headers: {
                    "version_name": "1.0.6",
                    "version_code": "6",
                    "package_name": "com.app.nanguatv",
                    "sign": md5("c431ea542cee9679#uBFszdEM0oL0JRn@"+t).toUpperCase(),
                    "imei": "c431ea542cee9679",
                    "timeMillis": t,
                    "User-Agent": "okhttp/4.6.0"
                },
                method: "get"
            });
            //log(jData)
            return JSON.parse(jData).data.url
        }
        if (/,/.test(input)) {
            showLoading('å¤šçº¿è·¯å¹¶å‘è§£æä¸­â€¦');
            var results = [];
            var names = [];
            var parseUrl = input.split(",");
            var task = function(obj) {
                //log(obj.url)
                var playdata = getvideo(obj.url)
                return playdata
            };
            var tasks = [];
            for (var i = 0; i < parseUrl.length; i++) {
                var otask = {
                    func: task,
                    param: {
                        url: parseUrl[i],
                    },
                    id: "è§£æ" + (i + 1)
                };
                tasks.push(otask);
            }
            var count = tasks.length;

            be(tasks, {
                func: function(obj, id, error, taskResult) {
                    count = count - 1;
                    //log(taskResult)
                    if (count > 0) {
                        showLoading("è§£æä¸­ï¼Œå‰©ä½™ä»»åŠ¡ï¼š" + count)
                        obj.results.push(taskResult + "#isVideo=true#");
                        obj.names.push(id);                       
                    } else {
                        hideLoading();
                    }

                },
                param: {
                    hi: "ccc",
                    results: results,
                    names: names
                }
            }, count);

            return JSON.stringify({
                urls: results,
                names: names
            })
        } else {
            let videoUrl = getvideo(input);
            return videoUrl
        }
    } catch (e) {
        log(e.toString())
        return 'toast://è§£æå¤±è´¥ï¼Œè¯·æ£€æŸ¥æ—¥å¿—'
    }

});

