    function aesTemplate(data, key, iv, mode, isEncrypt, isHex) {
        var test = loadJavaClass('hiker://files/cache/bidi.dex', 'com.rule.NumConvertUtil');

        if (!mode) {
            mode = "AES/CBC/PKCS5Padding"
        }

        if (/ECB/.test(mode)) {
            try {
                var keySpec = new SecretKeySpec(String(key).getBytes(), "AES");
                var cipher = Cipher.getInstance(mode);
                cipher.init(isEncrypt ? 1 : 2, keySpec);
                if (isEncrypt) {
                    if (isHex) {
                        return Tool.Bytes2HexString(cipher.doFinal(String(data).getBytes()));
                    } else {
                        return _base64.encodeToString(cipher.doFinal(String(data).getBytes()), _base64.NO_WRAP);
                    }
                } else {
                    if (isHex) {
                        return String(cipher.doFinal(test.hexStringToByte(data)));
                    } else {
                        return String(cipher.doFinal(_base64.decode(data, _base64.NO_WRAP)));
                    }
                }
            } catch (e) {
                return "" + e;
            }

        } else {
            try {
