        if (playJson.playid.search(/QLIVE|web_mp4|web_m3u8|PC-XXX|WMVBRH|WMZD|WMQPIC|WMXIAOMI/) >= 0) {
            if (playJson.vurl[0] == '/') {
                play = decodeURIComponent('http:' + vurl)
