var lazy = $("").lazyRule((MY_HOME) => {
    var player = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1]);
    var jsurl = player.url;
    if (player.encrypt == '1') {
        var jsurl = unescape(jsurl);
    } else if (player.encrypt == '2') {
        var jsurl = unescape(base64Decode(jsurl));
    } else {
        jsurl
    }
    var from = player.from;
    eval(fetch(MY_HOME + "/static/js/playerconfig.js"));
    var Parse = MacPlayerConfig.player_list[from].parse == '' ? MacPlayerConfig.parse : MacPlayerConfig.player_list[from].parse;
    if (Parse == '') {
        var pshtml = fetch(MY_HOME + "/static/player/" + from + ".js");
        Parse = pdfh(pshtml, "iframe&&src").split("=")[0] + "="
    }
    if(Parse.startsWith("/")){
        Parse = MY_HOME + Parse
    }
    var jxurl = Parse + jsurl;    
    log(jxurl)

    function GetPlayUrl(playUrl) {
        if (/mgtv|sohu/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)}";
        } else if (/bili/.test(playUrl)) {
            return playUrl + ";{Referer@https://www.bilibili.com}";
        } else if (/ixigua/.test(playUrl)) {
            return playUrl + ";{User-Agent@Mozilla/5.0 (Windows NT 10.0)&&Referer@https://www.ixigua.com/}";
        } else {
            return playUrl + "#isVideo=true#";
        }
    }

    var html = request(jxurl, {
        headers: {
            "Referer": MY_HOME
        },
        method: "get"
    });
    try{
    eval(html.match(/var config = {[\s\S]*?}/)[0] + '');    
    //let sethtml = fetch("https://p.tjomet.com/duoduo/js/setting.js");
    let sethtml = fetch(Parse.split('?')[0] + 'js/setting.js');
    eval("var adata = " + sethtml.match(/;var __O.*?=(.*?)]/)[1] + "]");   
