        headers: {       
            'origin': jxurl
        },
        body: "url=" + config.url + "&vkey=" + config.vkey + "&token=" + config.token + "&sign=" + adata[8],
        method: "POST"
    })).url;
    //log(url)
    eval(fetch(Parse.split("?")[0] + "js/decode.js"));
    return GetPlayUrl(getVideoInfo(url))
    }catch(e){
        eval(fetch("https://vkceyugu.cdn.bspapp.com/VKCEYUGU-03ee1f89-f0d4-49aa-a2b3-50e203514d8a/2e54cc42-9b4c-457d-b2de-0cdf3e2aeeaa.js"))
        var urls = html.match(/var urls = '(.*?)'/)[1];
        //log(urls)
        let playUrl = getVideoInfo(urls);
        //log(playUrl)
        return GetPlayUrl(playUrl)        
    }
}, MY_HOME);
