function AESDcode(cipherText) {
    eval(getCryptoJS());
    function hexToString(hexCharCodeStr) {ã€€ã€€
        var trimedStr = hexCharCodeStr.trim();ã€€ã€€
        var rawStr = ã€€ã€€trimedStr.substr(0, 2).toLowerCase() === "0x"ã€€ã€€ ? ã€€ã€€trimedStr.substr(2)ã€€ã€€ : ã€€ã€€trimedStr;ã€€ã€€
        var len = rawStr.length;ã€€ã€€
        if (len % 2 !== 0) {ã€€ã€€ã€€ã€€
            alert("Illegal Format ASCII Code!");ã€€ã€€ã€€ã€€
            return "";ã€€ã€€
        }ã€€ã€€
        var curCharCode;ã€€ã€€
        var resultStr = [];ã€€ã€€
        for (var i = 0; i < len; i = i + 2) {ã€€ã€€ã€€ã€€
            curCharCode = parseInt(rawStr.substr(i, 2), 16); // ASCII Code Value
            ã€€ã€€ã€€ã€€
            resultStr.push(String.fromCharCode(curCharCode));ã€€ã€€
        }ã€€ã€€
        return resultStr.join("");
    }

    //key = CryptoJS.enc.Utf8.parse("XRON915SDykBsraP");
    //iv = CryptoJS.enc.Utf8.parse("COvtX0dFbgD3NcAh");
    key = CryptoJS.enc.Utf8.parse("hmakpS3eDObWT7cQ");
    iv = CryptoJS.enc.Utf8.parse("idfTB6t3lqsQIL4S");
    cipherText = hexToString(cipherText);
    var decrypted = CryptoJS.AES.decrypt(cipherText, key, {
        iv: iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.Pkcs7
    });
    return decrypted.toString(CryptoJS.enc.Utf8);
}

