[{"col_type":"movie_3","name":"notice","path":"notice","rule":"function getNotice(lp) {
    return 'x5Rule://' + 'hiker://empty' + '@' + $.toString((lp) => {
        if (window.inject == null) {
            window.inject = true;
            eval(fy_bridge_app.fetch("hiker://files/rules/huya/huya_lib.js"));

            const ws = new WebSocket("wss://cdnws.api.huya.com/");
            ws.onopen = function(event) {
                var livingInfo = new HUYA.GetPresenterLiveScheduleInfoReq();
                livingInfo.lPresenterId = lp;
                sendWup("presenterui", "getPresenterLiveScheduleInfo", livingInfo);
            }
            ws.onmessage = function(event) {
                try {
                    let fr
                    fr = new FileReader();
                    fr.onload = function() {
                        let data = this.result
                        let stream = new Taf.JceInputStream(data);
                        let command = new HUYA.WebSocketCommand();
                        command.readFrom(stream);
                        switch (command.iCmdType) {
                            case HUYA.EWebSocketCommandType.EWSCmd_WupRsp:
                                try {
                                    let wup = new Taf.Wup();
                                    wup.decode(command.vData.buffer);
                                    let map = new(TafMx.WupMapping[wup.sFuncName])();
                                    wup.readStruct('tRsp', map, TafMx.WupMapping[wup.sFuncName]);
                                    msg = map.sSchedule + ',' + map.sDescription+'.' + map.sMessage;

                                    window.uu = msg
                                    ws.close()
                                } catch (e) {
                                    console.log(e)
                                }
                                break;
                            default:
                                break
                        }
                    }
                    fr.readAsArrayBuffer(event.data);
                } catch (e) {
                    console.log(e)
                }
            };

            function sendWup(action, callback, req) {
                try {
                    let wup = new Taf.Wup()
                    wup.setServant(action)
                    wup.setFunc(callback)
                    wup.writeStruct("tReq", req)
                    let command = new HUYA.WebSocketCommand()
                    command.iCmdType = HUYA.EWebSocketCommandType.EWSCmd_WupReq
                    command.vData = wup.encode()
                    let stream = new Taf.JceOutputStream()
                    command.writeTo(stream)
                    ws.send(stream.getBuffer())

                } catch (err) {
                    console.log(err)
                }
            }
        } else if (window.uu != null) {

            return "confirm://" + window.uu + ".js:'toast://确认'";
        }
    }, lp);
}
$.exports.getNotice = text => getNotice(text);"},{"col_type":"movie_3","name":"mobile","path":"mobile","rule":"function live(e) {
    let query = e.split('?');
    let i = query[0];
    let b = query[1];
    let r = i.split('/');
    let s = r[r.length - 1].replace(/\.(flv|m3u8)/g, '');
    let c = b.split('&', 4);
    let n = {};

    for (let i of c) {
        if (i != '') {
            let pair = i.split('=');
            n[pair[0]] = pair[1];
        }
    }
    let fm = unescape(n['fm']);
    let u = base64Decode(fm);
    let p = u.split('_')[0];
    let f = (new Date()).valueOf()*10000+'';
    let l = n['wsTime'];
    let t = '0';
    let h = [p, t, s, f, l].join('_');
    let m = md5(h);
    let y = c[c.length - 1];
    let url = i + "?wsSecret=" + m + "&wsTime=" + l + "&u=" + t + "&seqid=" + f + "&" + y;
    url=getVar('huya$vidtype')=='.flv'?url.replace(/(hls|m3u8)/g,'flv'):url;
    return 'https:'+url;
}
$.exports.getVid = text => live(text);"}]

