var lazy = $('').lazyRule(() => {
    function getUrl(avid, cid, qn) {
        try {
            if (getMyVar('bf_cookie', '未定义') == '未定义') {
                var Cookie = JSON.parse(request("hiker://files/rules/cy/bili.txt")).cookie
            } else {
                var Cookie = getMyVar('bf_cookie');
            }
        } catch (e) {
            Cookie = JSON.parse(fetchCookie('https://www.bilibili.com/', {
                headers: {
                    "User-Agent": PC_UA,
                    "Referer": "https://www.bilibili.com/",
                },
                method: "GET"
            }))[1];
        }
        try {
            let u = request(buildUrl("http://api.bilibili.com/x/player/playurl", {
                "avid": avid,
                "cid": cid,
                "qn": qn,
                "fnver": 0,
                "fnval": 4048,
                "fourk": 1
            }), {
                headers: {
                    Cookie: Cookie
                }
            })
            return JSON.parse(u).data.dash
        } catch (e) {
            try {
                log('尝试更改请求接口...');
                let u = request(buildUrl("https://api.bilibili.com/pgc/player/web/playurl", {
                    "avid": avid,
                    "cid": cid,
                    "qn": qn,
                    "fnver": 0,
                    "fnval": 4048,
                    "fourk": 1
                }), {
                    headers: {
                        Cookie: Cookie
                    }
                })
                return JSON.parse(u).result.dash
            } catch (error) {
                log('cookie有没有可能失效了呢~');
                return '';
            }
        }
    }
    function biliLazy(id, cid, qn) {
        var dash = getUrl(id, cid, qn);
        videoList = [];
        audioList = [];
        nameList = [];
        headers = [];
        dash.audio.forEach(item => {
            audioList.push(item.baseUrl);
            headers.push({
                "Referer": "https://www.bilibili.com",
                "User-Agent": PC_UA
            });
        });
        dash.video.forEach(item => {
            if (item.codecid == 7) {
                videoList.push(item.baseUrl + "#isVideo=true#");
                if (item.id == 126) {
                    nameList.push('杜比视界');
                } else if (item.id == 125) {
                    nameList.push('HDR 真彩色');
                } else if (item.id == 120) {
                    nameList.push('4K超清');
                } else if (item.id == 116) {
                    nameList.push('1080P60高帧率');
                } else if (item.id == 112) {
                    nameList.push('1080P+高码率');
                } else if (item.id == 80) {
                    nameList.push('1080P高清');
                } else if (item.id == 74) {
                    nameList.push('720P60高帧率');
                } else if (item.id == 64) {
                    nameList.push('720P高清');
                } else if (item.id == 32) {
                    nameList.push('480P清晰');
                } else if (item.id == 16) {
                    nameList.push('360P流畅');
                } else if (item.id == 6) {
                    nameList.push('240P极速');
                }
            }
        });
        return JSON.stringify({
            urls: videoList,
            audioUrls: audioList,
            headers: headers,
            names: nameList,
            danmu: 'https://comment.bilibili.com/' + cid + '.xml'
        })
    }
    videoParms = findItem(input);
    aid = videoParms.extra.aid;
    bvid = videoParms.extra.bvid;
    cid = videoParms.extra.cid;
    badge = videoParms.extra.badge;
    return biliLazy(aid, cid, 116).replace(/;/g, "；；");
});
