var d = [];

var get_data = (url) => {
    var data = pdfa(request(url), 'body&&.clearfix');
    data = data.map((data) => {
        var return_cn = {};
        return_cn.name = pdfh(data, "h3&&Text");
        return_cn.list = pdfa(data, '.clearfix&&.col')
            .map((datas) => {
                return {
                    CN_title: pdfh(datas, 'p.truncate&&Text'),
                    title: pdfh(datas, 'p.truncate,1&&Text'),
                    Emoji: pdfh(datas, 'div.h1&&Text')
                }
            });
        return return_cn
    })
    saveFile('Emoji.json', JSON.stringify(data))
    return data
}

if (!fileExist('require.json')) {
    d.push({
        title: "搜索",
        url: "'hiker://search?s='+input+'&rule=' + MY_RULE.title",
        col_type: "input",
        desc: "搜索你想要的Emoji表情...",
    });
}
var data;
if (fileExist('Emoji.json')) {
    data = JSON.parse(readFile('Emoji.json'))
} else {
    data = get_data('https://emojixd.com/list')
}
var selection_Emoji = MY_RULE.title + "Emoji";
var Emoji_id = getItem(selection_Emoji, '0')


d.push({
    title: '““””<font color=#23AE6D>💠  刷新缓存：</font>',
    url: $().lazyRule((get_data) => {
        toast("开始刷新缓存")
        get_data('https://emojixd.com/list')
        toast("刷新缓存成功")
        refreshPage(false);
        return "hiker://empty";
    }, get_data),
    col_type: "scroll_button"
});


var bt_title = []
data.forEach(data => bt_title.push(data.name))
bt_title.unshift('全部')
bt_title.forEach((data, id) => {
    data = id == Emoji_id ? '““””<b><font color=#FA7298>' + data + '</font></b>' : data
    d.push({
        title: data,
        url: $('#noLoading#').lazyRule((id, selection_Emoji, Name) => {
            setItem(selection_Emoji, id + "")
            refreshPage(false);
            return "hiker://empty";
        }, id, selection_Emoji),
        col_type: "scroll_button"
    });
})


if (Emoji_id > 0) {
    data = [data[Emoji_id - 1]]
}

data.forEach((data) => {
    d.push({
        title: '\n' + data.name,
        col_type: "text_center_1"
    });
    data.list.forEach((datas) => {
        d.push({
            title: datas.Emoji + '  ' + datas.CN_title,
            col_type: "text_2",
            url: $('#noLoading#').lazyRule((Emoji) => {
                copy(Emoji)
                return "hiker://empty";
            }, datas.Emoji)
        });
    })
})

setResult(d);

