const IvParameterSpec = javax.crypto.spec.IvParameterSpec;
const SecretKeySpec = javax.crypto.spec.SecretKeySpec;
const KEY = new java.lang.String("saIZXc4yMvq0Iz56");
const f2319IV = new java.lang.String("kbJYtBJUECT0oyjo");
const KEY_ALGORITHM = "AES";
const AES_MODE = "AES/CBC/PKCS7Padding";
const FileUtil = com.example.hikerview.utils.FileUtil;

function decryptData(file2Bytes) {
    try {
        let secretKeySpec = new SecretKeySpec(KEY.getBytes(), KEY_ALGORITHM);
        let ivParameterSpec = new IvParameterSpec(f2319IV.getBytes());
        let cipher = Cipher.getInstance(AES_MODE);
        cipher.init(2, secretKeySpec, ivParameterSpec);
        return cipher.doFinal(file2Bytes);
        return new SoftReference(cipher.doFinal(file2Bytes)).get();
    } catch (e) {
        log(e.toString());
        return null;
    }
}

$.exports.imageDecrypt = function() {
    let bytes = FileUtil.toBytes(input);
    bytes = decryptData(bytes);
    return FileUtil.toInputStream(bytes);
}"}]

