                    "noncestr": noncestr,
                    "platform": "android",
                    "timestamp": timestamp,
                    "token": null,
                    "version": "9.9.9-release"
                }))
            },
            body: {
                params: __ ? _ : apiview._aesEncrypt(_)
            },
            method: "post",
            withHeaders: true
        });
        eval("str_Ajax =" + str_Ajax);
        try {
            str_Ajax = __ ? str_Ajax.body : apiview._aesDecrypt(str_Ajax.body, str_Ajax.headers.key[0]);
            eval("str_Ajax =" + str_Ajax);
            str_Ajax = str_Ajax.data;
        } catch (e) {
            str_Ajax = null;
            putMyVar(":initSel", parseInt(getMyVar(":initSel", "0")) + 1);
        };
        return str_Ajax;
    },
    _aesDecrypt: (aes_str, aes_key) => {
        eval(getCryptoJS());
        let _rsa_array = apiview._rsaDecrypt(aes_key);
        eval("_rsa_array =" + _rsa_array);
        eval("aes_str =" + aes_str);
        let aes_str = CryptoJS.enc.Hex.parse(aes_str);
        aes_str = CryptoJS.enc.Base64.stringify(aes_str);
        let str_key = CryptoJS.enc.Utf8.parse(_rsa_array.key);
        let str_iv = CryptoJS.enc.Utf8.parse(_rsa_array.iv);
        return CryptoJS.AES.decrypt(aes_str, str_key, {
            iv: str_iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
        }).toString(CryptoJS.enc.Utf8);
    },
    _aesEncrypt: (word) => {
        eval(getCryptoJS());
        let str_key = "A18rMoHZXx%1J7()";
        let str_iv = "*_IDCOv_lRXP-LTk";
        str_key = CryptoJS.enc.Utf8.parse(str_key);
        str_iv = CryptoJS.enc.Utf8.parse(str_iv);
        return CryptoJS.AES.encrypt(word, str_key, {
            iv: str_iv,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
        }).ciphertext.toString().toUpperCase();
    },
    _rsaDecrypt: (handleText) => rsaDecrypt(handleText, apiview._rsakey_Ajax(), {
        config: "RSA/ECB/PKCS1Padding",
        type: 2,
        long: 1,
        block: true
    }),
