                putMyVar(":tagi", "" + i);
                refreshPage();
                return "hiker://empty";
            }, i),
            col_type: "scroll_button"
        });
    });
    layout.push({
        url: '"hiker://search?s="+input',
        desc: "搜你想要的......",
        title: "搜索",
        col_type: "input",
        extra: {
            rules: $.toString((urlMap, mapi) => {
                let arr1 = [];
