var d = [];
var html = getResCode();

function de(input) {
    rv = window0.atob(input);
    rv = escape(rv);
    rv = decodeURIComponent(rv);
    return rv;
}

function red(text) {
    return '““' + text + '””';
}

var page = MY_URL.split('#')[1];

var tabs = pdfa(html, 'body&&.menu&&dl');
var contlist = [];

for (var i in tabs) {
    if (page == '1') {
        var tab = de(pdfh(tabs[i], 'dt&&Html').split("'")[1])
        d.push({
            title: getVar('ytytid', '0') == i ? red(tab) : tab,
            url: $(i).lazyRule(() => {
                putVar('ytytid', input);
                refreshPage();
                return 'hiker://empty';
            }),
            col_type: 'scroll_button'
        });
    }
    contlist[i] = [];
    var conts = pdfa(tabs[i], 'dl&&a:not([data-url])');
    for (var j in conts) {
        contlist[i].push({
            title: de(pdfh(conts[j], 'a&&Html').split("'")[1]),
            url: pd(conts[j], 'a&&href')

        });
    }
}

if (page == '1') {
    for (var i in contlist[getVar('ytytid', '0')]) {
        d.push({
            title: getVar(getVar('ytytid', '0'), '0') == i ? red(contlist[getVar('ytytid', '0')][i].title) : contlist[getVar('ytytid', '0')][i].title,
            url: $(i).lazyRule(() => {
                putVar(getVar('ytytid', '0'), input);
                refreshPage();
                return 'hiker://empty';
            }),
            col_type: 'flex_button',
        });
    }
}
var url = page != '1' ? (contlist[getVar('ytytid', '0')][getVar(getVar('ytytid', '0'), '0')].url + '?page=' + page) : (contlist[getVar('ytytid', '0')][getVar(getVar('ytytid', '0'), '0')].url);

var html = request(url);

for (var list of pdfa(html, '.channel-list&&dl:has(a)')) {
    if (/视频|图片/.test(pd(html, '.total&&Text'))) {
        d.push({
            title: de(pdfh(list, 'a&&title')),
            desc: pdfh(list, 'i&&Text'),
            img: pd(list, 'img&&data-original')+ '@js=' + $.toString((rule) => {
                const image = $.require("hiker://page/image?rule=" + rule);
                return image.imageDecrypt();
            }, MY_RULE.title),
            url: $(pd(list, 'a&&href')).lazyRule((MY_HOME) => {
                    var url = fetch(input, {});
                    if (url.includes("m3u8")) {
                        eval(pdfh(url,'script:containsData(playUrl)&&Html'))
                        return playUrl
                    } else {
                        var img = pdfa(url, '.pic&&img');
                        var pics = img.map(h => pdfh(h, 'img&&src'));
                        return "pics://" + pics.join("&&");
                    }
                },
                MY_HOME),
            col_type: 'movie_2'
        });

    } else {
        d.push({
            title: de(pdfh(list, 'a&&title')),
            url: `hiker://page/xisoshuo#readTheme#?小说=${pd(list, 'a&&href')}`,
            col_type: 'text_1'
        });
    }
}
setResult(d);

