function Traditionalized(cc){
		var str='';
		for(var i=0;i<cc.length;i++){
			if(charPYStr().indexOf(cc.charAt(i))!=-1)
				str+=ftPYStr().charAt(charPYStr().indexOf(cc.charAt(i)));
			else
				str+=cc.charAt(i);
		}
		return str;
	}

