var res = {};
var d = [];
require("hiker://page/jian");
html = getResCode();

var des_pic = pd(html, 'img.lazy&&src');
var des_title = Simplized(pdfh(html, 'img&&alt'));
var des_desc = Simplized(pdfh(html, '.intro-total&&Text!!倒序'));

d.push({
    title: des_title,
    desc: des_desc,
    pic_url: des_pic,
    url: MY_URL,
    col_type: 'movie_1_vertical_pic_blur'
});

d.push({
    col_type: 'line_blank'
});


var chapterList = [];
pdfa(html, '.chapter__list-box&&li').forEach(function(data) {
    chapterList.push({
        title: Simplized(pdfh(data, 'a&&Text')),
        url: pd(data, 'a&&href'),
    });
});

function jx() {
    var list = pdfa(fetch(input), 'body&&img');
    var pics = [];
    for (var i in list) {
        pics.push(pd(list[i], 'img&&src'))
    }
    return "pics://" + pics.join("&&");

}
let downloadlazy = $.toString(jx);
let lazy = $("").lazyRule(jx);
d.push({
    title: "下载漫画",
    url: 'hiker://page/download.view#noRecordHistory##noRefresh#?rule=本地资源管理',
    col_type: 'scroll_button',
    extra: {
        chapterList: chapterList,
        info: {
            bookName: des_title.split(/\s+/)[0], //漫画名称,
            ruleName: MY_RULE.title,
            bookTopPic: des_pic,
            parseCode: downloadlazy,

        },
        defaultView: "1"
    }
});
d.push({
    title: "本地书架",
    url: 'hiker://page/Bookrack.view#noRecordHistory#?rule=本地资源管理&ruleName='+MY_RULE.title,
    col_type: 'scroll_button',
});





var list = pdfa(html, '.chapter__list-box&&li');

d.push({
    title: '‘‘章节目录’’',
    col_type: 'text_center_1'
});

for (var i in list) {
    try {
        d.push({
            title: Simplized(pdfh(list[i], 'a&&Text')),
            url: pd(list[i], 'a&&href') + lazy,
            col_type: 'text_1'
        });
    } catch (e) {}
}

res.data = d;
setHomeResult(res);

