if (!getVar('maomiHost')) {
    let txt = request('https://cors.eu.org/www.maomiav.com/assets/js/custom/config.js');
    window = {};
    eval(txt)
    let lines = [window.line_1, window.line_2, window.line_3]
    let urls = [];
    for (l of lines)
        urls.push({
            'url': l,
            'options': {
                timeout: 1500,
                withHeaders: true,
                redirect: false
            }
        });
    try {
        var ress = batchFetch(urls);
    } catch (e) {}
    let host = lines[1];
    for (res of ress) {
        res = JSON.parse(res);
        if (res.statusCode != -1) {
            if (res.statusCode == 302 || res.statusCode == 301) {
                host = res.headers.location[0].match(/https*:\/\/[^\/]*\//)[0];
            }
            break;
        }
    }
    putVar('maomiHost', host);
}

