      var dd = {
        title: title,
        desc: desc,
        pic_url: img,
        url: url,
        content: content,
        col_type: rule.col_type,
        extra: {
          img: img,
        },
      };
      if (lazy != "") {
        dd["url"] = url + lazy;
      }
      arr.push(dd);
