    });
}

if (page == '1') {
    for (let i in contlist) {
        d.push({
            title: getMyVar('分类', '0')== i ? Color(contlist[i].title) : contlist[i].title,
            url: $(i).lazyRule(() => {
                putMyVar('分类', input);
                refreshPage();
                return 'hiker://empty';
            }),
            col_type: 'scroll_button',
        })
    }
}

let id = contlist[getMyVar('分类', "0")].url;

let config = 'https://store.' + host + '.com/facts/tag?id=' + id + '&limit=48&offset=' + (page - 1) * 48;

let data = JSON.parse(request(config));

for (let i in data) {
    try {
        multi = data[i].file.hls_resources.fl_cdn_multi;
    } catch(e) {
        multi = data[i].fc_facts[0].hls_resources.fl_cdn_multi
    }
    d.push({
        title: data[i].file.stuff.sf_name,
        pic_url: 'https://thumbs-015.' + host + '.com/videos/' + data[i].fc_file_id + '/' + data[i].fc_facts[0].fc_thumbs[0] + '.webp?w=480',
        url: $().lazyRule((host, multi)=>{
            let video = request('https://video.' + host + '.com/' + multi, {
                headers: {
                    Origin: 'https://beeg.com'
                }
            });
            let name = video.match(/\d+x\d+/g);
            let videos = video.match(/PRO.*\n.*/g);
            let urls = [];
            let names = [];
            let headers = [];
            for (let i in name) {
                if (/\/\//.test(videos[i])) {
                    key = 'https:' + videos[i].match(/\/\/.*/)[0];
                } else {
                    key = 'https://video.' + host + '.com/' + videos[i].match(/key.*/)[0];
                }
                urls.push(key)
                names.push(name[i])
                headers.push({'Origin': 'https://beeg.com'})
            }
            return JSON.stringify({
                urls: urls,
                headers: headers,
                names: names
            })
        }, host, multi),
        col_type: 'movie_2'
    })
}

setResult(d)

