function decryptData(t) {
    var o, i, a, r, s, c,
        d = 0,
        tt = "ABCD*EFGHIJKLMNOPQRSTUVWX#YZabcdefghijklmnopqrstuvwxyz1234567890",
        indexTable = {};
    var u = [];
    for (var index = 0; index < tt.length; index++) {
        indexTable[tt[index]] = index;
    }
    t = t.replace(/[^A-Za-z0-9\*\#]/g, "");

    var len = t.length;
    while (d < len) {
        a = indexTable[t[d++]];
        o = (15 & (r = indexTable[t[d++]])) << 4 | (s = indexTable[t[d++]]) >> 2;
        i = (3 & s) << 6 | (c = indexTable[t[d++]]);
        u.push(a << 2 | r >> 4);
        if (s !== 64) u.push(o);
        if (c !== 64) u.push(i);
    }
    return String.fromCharCode.apply(null, u);
}
