const liens = ["https://hjvdo.139592.com", "https://p.hjpfe1.com", "https://p.hjcfcf.com"];

function cacheProxy(liens) {
    try {
        MY_URL = base64Decode(MY_PARAMS.remoteUrl[0]);
        if (MY_PARAMS.nolien&&MY_PARAMS.nolien[0] === "true") {
            return JSON.stringify({
                headers: {
                    'Content-Type': 'application/vnd.apple.mpegurl',
                    'Location': MY_URL
                },
                statusCode: 302
            });
        }
        let length = Number(MY_PARAMS.length[0]);
        let m3u8Cache = cacheM3u8(MY_URL);
        let cachePath = m3u8Cache.split("##")[0];
        let context = fetch(cachePath);
        let lastUrl = context.match(/^(http.*?)[\s\S]#EXT-X-ENDLIST$/m)[1];

        let baseUrl = lastUrl.split("?");
        if (baseUrl.length !== 1) {
            baseUrl = baseUrl[0].replace(getHome(lastUrl), liens[MY_PARAMS.lien[0] || 0])
        } else {
            baseUrl = baseUrl[0];
        }
        baseUrl = baseUrl.replace(/(\d+).ts/, "");
        let s = Number(RegExp.$1);
        if (!s) {
            return context;
        }
        let fix = "";
        for (let i = s + 1; i <= length; i++) {
            fix += "#EXTINF:1.250000,\n" + baseUrl + i + ".ts\n";
        }
        context = context.replace("#EXT-X-ENDLIST", fix + "#EXT-X-ENDLIST");
        writeFile(cachePath, context)
        return JSON.stringify({
            headers: {
                'Content-Type': 'application/vnd.apple.mpegurl',
                'Location': cachePath
            },
            statusCode: 302
        });
    } catch (e) {
        log(e.toString());
    }
}

function cacheVip(remoteUrl, length, MY_HOME) {
    remoteUrl = fixUrl(remoteUrl, MY_HOME);
    let serve = startProxyServer($.toString(cacheProxy, liens));
    let playlist = {
        urls: [],
        names: []
    };
    remoteUrl=base64Encode(remoteUrl||"");
    if (length) {
        for (let i = 0; i < liens.length; i++) {
            playlist.urls.push(buildUrl(serve, {
                lien: "" + i,
                remoteUrl: remoteUrl,
                length: "" + length
            }) + "#isVideo=true#");
            playlist.names.push("线路" + (i + 1));
        }
    }
    playlist.urls.push(buildUrl(serve, {
        nolien: true,
        remoteUrl: remoteUrl,
    }) + "#isVideo=true#");
    playlist.names.push("原链接");
    return JSON.stringify(playlist);
}

function fixUrl(url, MY_HOME) {
    if (url.startsWith("http")) {
        return url;
    } else {
        return MY_HOME + url;
    }
}

function getCookies(MY_HOME) {
    let cm = android.webkit.CookieManager.getInstance();
    let cookie = String(cm.getCookie(MY_HOME));
    let cookies = {};
    if (cookie) {
        cookie.split(";").forEach(v => {
            v = v.split("=");
            cookies[v[0].trim()] = String(v[1]).trim();
        });
    }
    return cookies;
}

function getForId(pid, id, MY_HOME) {
    let getData = $.require("data").getData;
    let cookies = getCookies(MY_HOME);
    if (!cookies.uid) {
        toast("该域名需要登录")
        return "web://" + MY_HOME+"/login";
    }

    let res = post(MY_HOME + "/api/attachment", {
        body: JSON.stringify({
            "id": id,
            "resource_id": pid,
            "resource_type": "topic",
            "line": "normal"
        }),
        headers: {
            "x-user-id": cookies.uid,
            "x-user-token": cookies.token
        }
    });
    let data = getData(res);
    if (data.video_time_length) {
        return cacheVip(fixUrl(data.remoteUrl, MY_HOME), data.video_time_length, MY_HOME)
    } else {
        return fixUrl(data.remoteUrl, MY_HOME) + "#isVideo=true#";
    }
}
$.exports = {
    cacheVip,
    getForId
};"}]

