            //log($.stringify(rule))
            let input = item;
            if (rule.startsWith("(")) {
                eval("result = " + rule);
            } else {
                /**
                 * 还原成 $.toString(...) 的最终结果，达到最终处理方式跟上面的 if 一致的目的
                 */
                eval("result = " + "(() => {" + rule + "})()");
