  $(url).image(() => {
    const FileUtil = com.example.hikerview.utils.FileUtil;
    let bytes = FileUtil.toBytes(input);
    const Cipher = javax.crypto.Cipher;
    const SecretKeySpec = javax.crypto.spec.SecretKeySpec;
    const KEY = new java.lang.String('Jui7X#cdleN^3eZb');
    const KEY_ALGORITHM = 'AES';
    const AES_MODE = 'AES/ECB/NoPadding';
    let secretKeySpec = new SecretKeySpec(KEY.getBytes(), KEY_ALGORITHM);
    let cipher = Cipher.getInstance(AES_MODE);
    cipher.init(2, secretKeySpec);
    return FileUtil.toInputStream(cipher.doFinal(bytes));
  });
const _request = (url) => {
  const input = request(url, {
    headers: {
      'Accept-Encoding': 'gzip, deflate, br',
    },
    inputStream: true,
  });
  let bytes;
  const FileUtil = com.example.hikerview.utils.FileUtil;
  const GZIPInputStream = java.util.zip.GZIPInputStream;
  try {
    const gzip = new GZIPInputStream(input);
    bytes = FileUtil.toBytes(gzip);
  } catch (e) {
    bytes = FileUtil.toBytes(input);
  } finally {
    closeMe(input);
  }
  return new java.lang.String(bytes, 'UTF-8');
};
const lazyRule = (url) =>
  $(url).lazyRule((_request) => {
    const html = _request(input);
    const bad = '502 Bad Gateway';
    if (html.includes(bad)) {
      input = 'toast://' + bad;
    } else {
      input = pdfh(html, 'body&&iframe&&src').split('url=')[1];
    }
    return input;
  }, _request);
$.exports = { image, lazyRule, _request };
"}]

