var document = {};
var window = {
    location: {
        reload: function() {
            return;
        }
    }
};

// 定义一个对象用于存储 cookie 名称和值的键值对
var cookies = {};
Object.defineProperty(document, 'cookie', {
    enumerable: true,
    configurable: true,
    get: function() {
        return Object.entries(cookies).map(([key, value]) => `${key}=${value}`).join('; ');
    },
    set: function(value) {
        var list = [];
        if (value.includes(';')) {
            list = value.split(';');
        } else {
            list = [value];
        }
        list.forEach(x => {
            var index = x.indexOf("=");
            var key = x.substring(0, index).trim();
            var value = x.substring(index + 1);
            cookies[key] = value;
        })
    }
});

function guard(url, headobj) {
    var head = headobj ? headobj:{
        headers: {
            Cookie: getCookie(url),
        }
    }
    var html = fetch(url, head);
    if (html.includes('_guard/auto.js')) {
        document["cookie"] = getCookie(url);
        eval(fetch(getHome(url) + '/_guard/auto.js'))
        html = fetch(url, {
            headers: {
                Cookie: document["cookie"],
            }
        });
    }
    //log(document["cookie"])
    return html
}

function csplit(a, b, c) {
    b = parseInt(b, 10) || 76;
    c = c || '\r\n';
    if (b < 1) {
        return false
    }
    return a.match(new RegExp(".{0," + b + "}","g")).join(c)
}

function reurl(a) {
    var b = '';
    var c = csplit(a, 8).split('\r\n');
    for (var i = 0; i < (c.length - 1); i++) {
        b = b + String.fromCharCode((parseInt(c[i], 2) - 10).toString(10))
    }
    return b
}"}]

