            let rule = jsRule.replace("@js:",
                "");
            rule = rule.trim();
            //log($.stringify(rule))
            let input = item;
            if (rule.startsWith("(")) {
                eval("result = " + rule);
            } else {
                /**
                 * 还原成 $.toString(...) 的最终结果，达到最终处理方式跟上面的 if 一致的目的
                 */
                eval("result = " + "(() => {" + rule + "})()");
            }
            return result || "";
        },
        getTitle(src, category) {
            let title = "";
            let titleRule = category || "a&&Text";
            if (titleRule.startsWith("@js:")) {
                title = this.evalJSRule(src, titleRule);
            } else {
                title = parseDomForHtml(src, titleRule);
            }
            return title || "";
        },
        getUrl(src, category) {
            let url = "";
            let urlRule = category.链接 || "a&&href";
            if (urlRule.startsWith("@js:")) {
                url = this.evalJSRule(src, urlRule);
            } else {
                url = pd(src, urlRule);
            }
            if (typeof category.链接处理 == "function") {
                url = category.链接处理(url);
            }
            return url || "";
        },
        isNull(param) {
            if (param == null) {
                return this;
            }
            return false;
        },
        isBoolean(variable) {
            if (typeof variable !== "boolean") {
                throw new Error("开启折叠请传入布尔值");
            }
        },
        清除(list) {
            if (this.isNull(list)) {
                return this;
            }
            if (list.constructor == String) {
                throw new Error("清除报错:请传列表");
            }
            this.clears = list;
            return this;
        },
        全清(list) {
            if (this.isNull(list)) {
                return this;
            }
            var c = this.clearall;
            list = c.concat(list);
            this.clearall = list;
            return this;
        },
        源码(input) {
            this.html = input;
            return this;
        },
